mapper.select:
select CASE WHEN tu.is_vip= 'Y' THEN '是' ELSE '否' END as isVip, tu.USERNAME as username, xt.p_type as productionType, CASE WHEN tu.company_id= '0' THEN '个人' ELSE '企业' END as companyId, xp.*
from xg_production xp LEFT JOIN t_user tu on xp.user_id =tu.USER_ID left join xg_production_type xt on xt.id =xp.type
tu.USERNAME like '%${username}%'
and xp.create_date >= #{startTime} and xp.create_date <= #{endTime}
and xp.type =#{type}
and xp.status =#{status}

select xpt.p_type as productionType, tu.USERNAME as username, xp.* from xg_production xp LEFT join xg_production_type xpt on xp.type =xpt.id INNER JOIN t_user tu on tu.USER_ID =xp.user_id
xp.type= #{type}
and xp.status= #{status} and xp.status != 'I' AND xp.status != 'C' AND xp.status != 'D'
and xp.id= #{id}
and xp.user_id= #{userId}
ORDER BY xp.create_date desc

select xpt.p_type as productionType, tu.USERNAME as username, xp.* from (SELECT xpp.* FROM xg_production_collection xpc LEFT JOIN xg_production xpp on xpc.p_id = xpp.id where xpc.user_id =#{userId})
xp LEFT join xg_production_type xpt on xp.type =xpt.id INNER JOIN t_user tu on tu.USER_ID =xp.user_id
xp.type= #{type}
and xp.status= #{status} and xp.status != 'I' AND xp.status != 'C' AND xp.status != 'D'
and xp.id= #{id}
and xp.user_id= #{userId}
ORDER BY xp.create_date desc

select xpt.p_type as productionType, tu.USERNAME as username, xp.* from ( SELECT xpp.* from xg_user_fensi uf INNER JOIN xg_production xpp on uf.to_user_id =xpp.user_id where uf.from_user_id =#{userId}) xp LEFT join xg_production_type xpt on xp.type =xpt.id INNER JOIN t_user tu on tu.USER_ID =xp.user_id
xp.type= #{type}
and xp.status= #{status} and xp.status != 'I' AND xp.status != 'C' AND xp.status != 'D'
and xp.id= #{id}
ORDER BY xp.create_date desc
